mapper.select:
select

from StockInfo a where a.tokenContactAddr = #{addr}
and a.stocktype='pureSpot'

SELECT

FROM StockInfo a left join UserInfo b on a.createBy = b.id
left join UserInfo c on a.updateBy = c.id
WHERE 1=1

    and a.stockType = #{stockType}


    and a.stockCode = #{stockCode}


    and a.stockName like '%'||#{stockName}||'%'


    and a.canTrade = #{canTrade}


    and a.canBorrow = #{canBorrow}


    and a.isExchange = #{isExchange}


    and a.canWealth = #{canWealth}


    and a.id = #{id}


    and a.tradeStockinfoId = #{tradeStockinfoId}


    and a.capitalStockinfoId = #{capitalStockinfoId}


    and a.tokenContactAddr = #{tokenContactAddr}


    and a.remark = #{remark}


    and a.isActive='yes'

order by a.stockCode asc

select d.id as id,d.stockCode as stockCode from STOCKINFO d where id in(
  select distinct capitalstockinfoid from STOCKINFO a where a.cantrade='yes' and a.isActive='yes'
)
